prompts.body:
{
  "application": {
    "name": "Prompteka",
    "version": "1.2.0",
    "environment": "production",
    "build": {
      "number": 42,
      "timestamp": "2025-12-03T12:00:00Z",
      "git_commit": "a3f8d92",
      "branch": "main"
    }
  },
  "database": {
    "engine": "sqlite",
    "path": "~/Library/Application Support/prompteka/prompts.db",
    "settings": {
      "journal_mode": "WAL",
      "cache_size": 10000,
      "temp_store": "MEMORY"
    },
    "migrations": {
      "enabled": true,
      "version": 12,
      "history": [
        {"version": 1, "description": "Initial schema"},
        {"version": 12, "description": "Add emoji icons"}
      ]
    }
  },
  "ui": {
    "theme": {
      "default": "system",
      "colors": {
        "light": {
          "primary": "#3B82F6",
          "background": "#FFFFFF",
          "text": "#1F2937"
        },
        "dark": {
          "primary": "#60A5FA",
          "background": "#111827",
          "text": "#F9FAFB"
        }
      }
    },
    "window": {
      "default_size": {"width": 1200, "height": 800},
      "remember_position": true
    },
    "editor": {
      "font_family": "SF Mono, Monaco",
      "font_size": 14,
      "line_height": 1.6,
      "word_wrap": true
    }
  },
  "features": {
    "search": {
      "enabled": true,
      "full_text": true,
      "shortcuts": {
        "focus": "Cmd+F",
        "next": "Cmd+G"
      }
    },
    "preview": {
      "enabled": true,
      "formats": ["markdown", "html", "json", "xml"],
      "markdown": {
        "syntax_highlighting": true,
        "tables": true,
        "emoji": true
      },
      "html": {
        "sandboxed": true,
        "allow_scripts": false
      }
    },
    "organization": {
      "folders": {
        "enabled": true,
        "nested": true,
        "max_depth": 5
      },
      "tags": {
        "enabled": true,
        "color_coded": true
      },
      "icons": {
        "total_available": 269,
        "categories": 13
      }
    }
  },
  "performance": {
    "cache": {
      "enabled": true,
      "size_mb": 50
    },
    "debounce": {
      "search_ms": 300,
      "auto_save_ms": 500
    }
  }
}
